mapper.select:
select al.alarmId, al.NodeType, olte.Name as OltId, al.SlotId, al.PONIfId, al.ONUId, al.RMid, al.PortType, al.MonitorEntityId, al.VCI, al.VPI, al.AlarmType, al.AlarmLevel, al.AlarmKind, FROM_UNIXTIME(GenTime,'%Y-%m-%d %h:%i:%s') as GenTime, FROM_UNIXTIME(ClearTime,'%Y-%m-%d %h:%i:%s') as ClearTime, FROM_UNIXTIME(AffirmTime,'%Y-%m-%d %h:%i:%s') as AffirmTime, al.AffirmState, urc.userName as UserIdAffirmer, al.ReasonForClear, al.userIdClear from alarmmgrrm al left join oltequiprm olte on CONCAT('CF::FSAP::OLTEquipRM:',al.OltId)=olte.dn left join useraccountmgr urc on al.UserIdAffirmer = urc.userId
state = #{alarmClazz}
AND AlarmType = #{alarmType}
AND AlarmLevel = #{alarmLevel}
AND AffirmState = #{confirmState}
AND FROM_UNIXTIME(GenTime,'%Y-%m-%d %h:%i:%s') > #{startTime}
AND DATE_ADD(#{endTime}, INTERVAL 1 DAY) > FROM_UNIXTIME(GenTime,'%Y-%m-%d %h:%i:%s')
AND alarmId != 0
order by GenTime DESC limit ${(page.curPage-1)*page.numPerPage}, #{page.numPerPage}

		select count(*) 
		from alarmmgrrm
		
			
				state = #{alarmClazz}
			
			
				AND AlarmType = #{alarmType}
			
			
				AND AlarmLevel = #{alarmLevel}
			
			
				AND AffirmState = #{confirmState}
			
			
				AND FROM_UNIXTIME(GenTime,'%Y-%m-%d %h:%i:%s') > #{startTime}
			
			
				AND  DATE_ADD(#{endTime}, INTERVAL 1 DAY) > FROM_UNIXTIME(GenTime,'%Y-%m-%d %h:%i:%s')
 			
			AND alarmId != 0

select count(*) 
from alarmmgrrm
where AlarmLevel = #{level}
